import React, { useState } from 'react';
import axios from 'axios';

export default function SolicitudForm({ onCreated }) {
  const [form, setForm] = useState({
    nombre: '',
    grupo: '',
    motivo: '',
    fecha_ausencia: ''
  });
  const [loading, setLoading] = useState(false);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });
  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/solicitudes`, form);
      setForm({ nombre: '', grupo: '', motivo: '', fecha_ausencia: '' });
      onCreated?.(res.data);
      alert('Solicitud enviada');
    } catch (err) {
      alert('Error al enviar');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input name="nombre" placeholder="Nombre" value={form.nombre} onChange={handleChange} required />
      <input name="grupo" placeholder="Grupo" value={form.grupo} onChange={handleChange} required />
      <textarea name="motivo" placeholder="Motivo1234" value={form.motivo} onChange={handleChange} required />
      <input name="fecha_ausencia" type="date" value={form.fecha_ausencia} onChange={handleChange} required />
      <button type="submit" disabled={loading}>{loading ? 'Enviando...' : 'Enviar'}</button>
    </form>
  );
}
