import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function SolicitudesList() {
  const [solicitudes, setSolicitudes] = useState([]);
  useEffect(() => {
    axios.get(`${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/solicitudes`)
      .then(res => setSolicitudes(res.data))
      .catch(console.error);
  }, []);
  return (
    <div>
      <h3>Solicitudes</h3>
      <ul>
        {solicitudes.map(s => (
          <li key={s.ida}>
            <strong>{s.nombre}</strong> ({s.grupo}) - {new Date(s.fecha_ausencia).toLocaleDateString()} - {s.motivo}
          </li>
        ))}
      </ul>
    </div>
  );
}
