import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Sol.css'; // Archivo de estilos

export default function SolicitudesList() {
  const [solicitudes, setSolicitudes] = useState([]);

  useEffect(() => {
    const fetchSolicitudes = async () => {
      try {
        const res = await axios.get(
          `${process.env.REACT_APP_API_URL || 'http://localhost:4000'}/solicitudes`
        );
        setSolicitudes(res.data);
      } catch (err) {
        console.error('Error al obtener solicitudes:', err);
      }
    };

    fetchSolicitudes();
  }, []);

  return (
    <div className="solicitudes-container">
      <h3>Solicitudes</h3>
      {solicitudes.length === 0 ? (
        <p>No hay solicitudes.</p>
      ) : (
        <ul className="solicitudes-list">
          {solicitudes.map((s) => (
            <li key={s.id} className="solicitud-item">
              <div className="solicitud-header">
                <strong>{s.nombre}</strong> ({s.grupo})
                <span className="fecha">{new Date(s.fecha_ausencia).toLocaleDateString()}</span>
              </div>
              <p className="motivo">{s.motivo}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
